// SVG icons mapping will be defined after inline components

interface IIcon {
  className?: string;
  color?: string;
  disabled?: boolean;
  id?: string;
  isSvg?: boolean;
  name: string;
  opacity?: number;
  rotate?: number;
  size?: number | string;
  wrapperClassname?: string;
}

export const Icon: FC<IIcon> = ({
  className,
  color,
  id,
  name,
  rotate,
  size,
  wrapperClassname,
  disabled,
  opacity,
  isSvg,
}) => {
  const Component = icons[name];
  const svgUrl = svgIcons[name];

  // Check if svgUrl is a React component (function or memoized component)
  const isInlineSvgComponent =
    typeof svgUrl === 'function' ||
    (svgUrl &&
      typeof svgUrl === 'object' &&
      svgUrl.$$typeof === Symbol.for('react.memo'));

  return (
    <div className={wrapperClassname} id={id}>
      {Component && !isSvg ? (
        <Component
          className={className}
          style={{ fontSize: size, color, opacity }}
          rotate={rotate}
          disabled={disabled}
        />
      ) : isInlineSvgComponent ? (
        React.createElement(svgUrl as React.ComponentType<any>, {
          className: className || '',
          style: {
            width: size,
            height: size,
            color,
            opacity,
            transform: rotate ? `rotate(${rotate}deg)` : undefined,
          },
        })
      ) : svgUrl ? (
        <img
          className={className}
          src={svgUrl}
          alt={name}
          style={{
            width: size,
            height: size,
            color,
            opacity,
            transform: rotate ? `rotate(${rotate}deg)` : undefined,
          }}
        />
      ) : (
        // Fallback for any remaining icons
        <div
          className={className}
          style={{ width: size, height: size, opacity }}
        >
          {name}
        </div>
      )}
    </div>
  );
};

